import React, { useState, useEffect } from 'react';

// Header Component with Cart Counter
const Header = ({ cartCount }) => {
  return (
    <header style={headerStyle}>
      <h1>ShopEasy</h1>
      <nav>
        <a href="#">Home</a>
        <a href="#">Shop</a>
        <a href="#">About</a>
        <div style={cartIconStyle}>
          <span>ðŸ›’</span>
          {cartCount > 0 && <span style={badgeStyle}>{cartCount}</span>}
        </div>
      </nav>
    </header>
  );
};

// Product Card Component for Shop
const ProductCard = ({ product, onAddToCart }) => {
  return (
    <div style={productCardStyle}>
      <img src={product.image} alt={product.name} style={productImageStyle} />
      <h3>{product.name}</h3>
      <p>${product.price.toFixed(2)}</p>
      <button 
        style={addButtonStyle}
        onClick={() => onAddToCart(product)}
      >
        Add to Cart
      </button>
    </div>
  );
};

// Cart Item Component
const CartItem = ({ item, onUpdateQuantity, onRemoveItem }) => {
  return (
    <div style={cartItemStyle}>
      <img src={item.image} alt={item.name} style={cartItemImageStyle} />
      <div style={cartItemDetailsStyle}>
        <h4>{item.name}</h4>
        <p>${item.price.toFixed(2)}</p>
        <div style={quantityControlStyle}>
          <button 
            onClick={() => onUpdateQuantity(item.id, item.quantity - 1)}
            disabled={item.quantity <= 1}
          >
            âˆ’
          </button>
          <span style={quantityStyle}>{item.quantity}</span>
          <button 
            onClick={() => onUpdateQuantity(item.id, item.quantity + 1)}
          >
            +
          </button>
        </div>
        <button 
          style={removeButtonStyle}
          onClick={() => onRemoveItem(item.id)}
        >
          Remove
        </button>
      </div>
    </div>
  );
};

// Shop Page Component
const Shop = ({ onAddToCart }) => {
  // Sample product data
  const [products] = useState([
    {
      id: 1,
      name: "Wireless Headphones",
      price: 89.99,
      image: "https://via.placeholder.com/200x200?text=Headphones"
    },
    {
      id: 2,
      name: "Smart Watch",
      price: 149.99,
      image: "https://via.placeholder.com/200x200?text=Smart+Watch"
    },
    {
      id: 3,
      name: "Bluetooth Speaker",
      price: 59.99,
      image: "https://via.placeholder.com/200x200?text=Speaker"
    },
    {
      id: 4,
      name: "Phone Case",
      price: 24.99,
      image: "https://via.placeholder.com/200x200?text=Phone+Case"
    },
    {
      id: 5,
      name: "USB-C Cable",
      price: 19.99,
      image: "https://via.placeholder.com/200x200?text=USB+Cable"
    },
    {
      id: 6,
      name: "Wireless Charger",
      price: 39.99,
      image: "https://via.placeholder.com/200x200?text=Charger"
    }
  ]);

  return (
    <main style={shopMainStyle}>
      <h2>Our Products</h2>
      <div style={productsGridStyle}>
        {products.map(product => (
          <ProductCard 
            key={product.id} 
            product={product} 
            onAddToCart={onAddToCart}
          />
        ))}
      </div>
    </main>
  );
};

// Cart Page Component
const Cart = ({ cartItems, onUpdateQuantity, onRemoveItem }) => {
  const subtotal = cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  const tax = subtotal * 0.08;
  const total = subtotal + tax;

  return (
    <main style={cartMainStyle}>
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p style={emptyCartStyle}>Your cart is empty. Start shopping!</p>
      ) : (
        <div style={cartContainerStyle}>
          <div style={cartItemsStyle}>
            {cartItems.map(item => (
              <CartItem 
                key={item.id} 
                item={item} 
                onUpdateQuantity={onUpdateQuantity}
                onRemoveItem={onRemoveItem}
              />
            ))}
          </div>
          <div style={summaryStyle}>
            <h3>Order Summary</h3>
            <div style={summaryRowStyle}>
              <span>Subtotal:</span>
              <span>${subtotal.toFixed(2)}</span>
            </div>
            <div style={summaryRowStyle}>
              <span>Tax (8%):</span>
              <span>${tax.toFixed(2)}</span>
            </div>
            <div style={{...summaryRowStyle, ...totalStyle}}>
              <span>Total:</span>
              <span>${total.toFixed(2)}</span>
            </div>
            <button style={checkoutButtonStyle}>Proceed to Checkout</button>
          </div>
        </div>
      )}
    </main>
  );
};

// Main App Component
const App = () => {
  const [currentPage, setCurrentPage] = useState('shop');
  const [cartItems, setCartItems] = useState([]);

  // Calculate total items in cart
  const cartCount = cartItems.reduce((count, item) => count + item.quantity, 0);

  // Add item to cart
  const handleAddToCart = (product) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item.id === product.id);
      
      if (existingItem) {
        return prevItems.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevItems, { ...product, quantity: 1 }];
      }
    });
  };

  // Update item quantity
  const handleUpdateQuantity = (id, newQuantity) => {
    if (newQuantity < 1) return;
    
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === id
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  // Remove item from cart
  const handleRemoveItem = (id) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== id));
  };

  return (
    <div style={appStyle}>
      <Header cartCount={cartCount} />
      <div style={navTabsStyle}>
        <button 
          style={{...tabStyle, ...(currentPage === 'shop' && activeTabStyle)}}
          onClick={() => setCurrentPage('shop')}
        >
          Shop
        </button>
        <button 
          style={{...tabStyle, ...(currentPage === 'cart' && activeTabStyle)}}
          onClick={() => setCurrentPage('cart')}
        >
          Cart ({cartCount})
        </button>
      </div>
      
      {currentPage === 'shop' ? (
        <Shop onAddToCart={handleAddToCart} />
      ) : (
        <Cart 
          cartItems={cartItems} 
          onUpdateQuantity={handleUpdateQuantity}
          onRemoveItem={handleRemoveItem}
        />
      )}
    </div>
  );
};

