// ProductDetail.jsx
import React from 'react';
import { useParams, Link } from 'react-router-dom';
import './ProductDetail.css';

const ProductDetail = () => {
    const { id } = useParams();

    // In a real app, you would fetch product data based on ID
    // For now, we'll use sample data
    const product = {
        id: 1,
        title: 'Classic White T-Shirt',
        price: 24.99,
        image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80',
        category: 'men',
        description: 'Premium cotton t-shirt with perfect fit for everyday wear.',
        details: '100% cotton, machine washable, imported'
    };

    const addToCart = () => {
        // Add to cart logic similar to Shop page
        console.log(`Added product ${id} to cart`);
    };

    return (
        <main>
            <div className="breadcrumb">
                <Link to="/">Home</Link> / <Link to="/shop">Shop</Link> / <span>{product.title}</span>
            </div>

            <div className="product-detail">
                <div className="product-image">
                    <img src={product.image} alt={product.title} />
                </div>

                <div className="product-info">
                    <h1>{product.title}</h1>
                    <p className="product-category">{product.category}</p>
                    <p className="product-price">${product.price.toFixed(2)}</p>
                    <p className="product-description">{product.description}</p>
                    <p className="product-details">{product.details}</p>

                    <button className="add-to-cart-btn" onClick={addToCart}>
                        Add to Cart
                    </button>
                </div>
            </div>
        </main>
    );
};

export default ProductDetail;
