import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useParams, useNavigate } from 'react-router-dom';

// Product Data (same for both components)
const products = [
  { id: 1, title: 'Classic White T-Shirt', price: 24.99, image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Premium cotton t-shirt with perfect fit for everyday wear.', details: '100% premium cotton, machine washable, available in all sizes. Our classic white t-shirt is made from high-quality combed cotton for extra softness and durability. Perfect for layering or wearing on its own.' },
  { id: 2, title: 'Slim Fit Jeans', price: 59.99, image: 'https://images.unsplash.com/photo-1542272604-787c3835535d?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Comfortable slim fit jeans with stretch for all-day comfort.', details: '98% cotton, 2% elastane, stretch denim, five-pocket style. These jeans feature a modern slim fit that is both stylish and comfortable. The stretch material allows for ease of movement throughout your day.' },
  { id: 3, title: 'Summer Floral Dress', price: 45.99, image: 'https://images.unsplash.com/photo-1515372039744-b8f02a3ae446?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'women', description: 'Lightweight floral dress perfect for summer occasions.', details: 'Lightweight chiffon fabric, floral pattern, available in multiple colors. This beautiful dress features a flattering A-line cut and delicate floral print that is perfect for garden parties, weddings, or any special occasion.' },
  { id: 4, title: 'Designer Handbag', price: 89.99, image: 'https://images.unsplash.com/photo-1584917865442-de89df76afd3?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'bags', description: 'Elegant handbag with ample space for all your essentials.', details: 'Genuine leather, gold-tone hardware, inner zip pocket. This sophisticated handbag features multiple compartments to keep your belongings organized, with a secure zip closure and adjustable strap for shoulder or crossbody wear.' },
  { id: 5, title: 'Running Shoes', price: 79.99, image: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'shoes', description: 'High-performance running shoes with cushion technology.', details: 'Breathable mesh upper, cushioned midsole, rubber outsole for traction. Designed for serious runners, these shoes provide excellent support and shock absorption with every step. The lightweight design helps improve performance without sacrificing comfort.' },
  { id: 6, title: 'Leather Jacket', price: 129.99, image: 'https://images.unsplash.com/photo-1551028719-00167b16eac5?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Genuine leather jacket with a classic biker style.', details: '100% genuine leather, front zip closure, ribbed cuffs and waistband. This timeless jacket features multiple pockets, a comfortable lining, and a rugged yet stylish appearance that only improves with age.' },
  { id: 7, title: 'High Heels', price: 64.99, image: 'https://images.unsplash.com/photo-1543163521-1bf539c55dd2?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'shoes', description: 'Elegant high heels for special occasions.', details: 'Suede material, 3-inch heel, pointed toe design. These stunning heels feature a comfortable padded insole and a secure ankle strap for stability. Perfect for evening events or adding a touch of elegance to your outfit.' },
  { id: 8, title: 'Crossbody Bag', price: 49.99, image: 'https://images.unsplash.com/photo-1591561954557-26941169b49e?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'bags', description: 'Compact crossbody bag with adjustable strap.', details: 'Faux leather, adjustable strap, multiple interior pockets. This versatile bag is perfect for keeping your essentials secure while leaving your hands free. The compact design makes it ideal for daily use or travel.' },
  { id: 9, title: 'Casual Blouse', price: 39.99, image: 'https://images.unsplash.com/photo-1594633312681-425c7b97ccd1?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'women', description: 'Comfortable and stylish blouse for everyday wear.', details: 'Soft viscose fabric, button-front design, relaxed fit. This blouse features a flattering cut that drapes beautifully, with delicate details like pearl buttons and a slightly tapered waist for a polished look.' },
  { id: 10, title: 'Sports Shorts', price: 34.99, image: 'https://images.unsplash.com/photo-1506629905877-52a5ca6d63b1?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Breathable sports shorts for your active lifestyle.', details: 'Lightweight polyester, elastic waistband with drawstring, side pockets. Designed for maximum mobility and comfort during physical activity, these shorts wick moisture away from the body and feature a secure pocket for small essentials.' },
  { id: 11, title: 'Earrings Set', price: 29.99, image: 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'accessories', description: 'Beautiful set of earrings for any occasion.', details: 'Hypoallergenic materials, set of 3 different styles. This versatile set includes studs, hoops, and drop earrings that can be mixed and matched to complement any outfit. Made with sensitive ears in mind.' },
  { id: 12, title: 'Sunglasses', price: 89.99, image: 'https://images.unsplash.com/photo-1572635196237-14b3f281503f?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'accessories', description: 'Stylish sunglasses with UV protection.', details: '100% UV protection, polarized lenses, lightweight frame. These fashionable sunglasses reduce glare and protect your eyes from harmful UV rays. The durable frame is designed for all-day comfort and long-lasting wear.' },
];

// Shop Component
const Shop = ({ addToCart, cartItems }) => {
  const navigate = useNavigate();
  const [activeCategory, setActiveCategory] = useState('all');
  const [sortBy, setSortBy] = useState('default');

  // Filter products by category
  const filteredProducts = activeCategory === 'all' 
    ? products 
    : products.filter(product => product.category === activeCategory);

  // Sort products
  const sortedProducts = [...filteredProducts].sort((a, b) => {
    if (sortBy === 'price-low') return a.price - b.price;
    if (sortBy === 'price-high') return b.price - a.price;
    if (sortBy === 'name') return a.title.localeCompare(b.title);
    return 0; // default sort (no sorting)
  });

  // Add to cart function
  const handleAddToCart = (product, e) => {
    e.stopPropagation(); // Prevent event from bubbling to the card
    addToCart(product);

    // Show notification
    const notification = document.createElement('div');
    notification.className = 'cart-notification';
    notification.innerHTML = `
      <span>${product.title} added to cart!</span>
    `;
    document.body.appendChild(notification);

    // Remove notification after 2 seconds
    setTimeout(() => {
      notification.classList.add('fade-out');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 2000);
  };

  // Navigate to product detail page
  const viewProductDetails = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div className="shop-container">
      <div className="shop-banner">
        <h1>Shop Our Collection</h1>
        <p>Discover the latest trends in fashion</p>
      </div>

      <div className="shop-stats">
        <p>Showing {sortedProducts.length} of {products.length} products</p>
        <p>Total items in cart: {cartItems.reduce((total, item) => total + item.quantity, 0)}</p>
      </div>

      <div className="shop-controls">
        <div className="category-filter">
          <button 
            className={activeCategory === 'all' ? 'active' : ''} 
            onClick={() => setActiveCategory('all')}
          >
            All Products
          </button>
          <button 
            className={activeCategory === 'men' ? 'active' : ''} 
            onClick={() => setActiveCategory('men')}
          >
            Men
          </button>
          <button 
            className={activeCategory === 'women' ? 'active' : ''} 
            onClick={() => setActiveCategory('women')}
          >
            Women
          </button>
          <button 
            className={activeCategory === 'shoes' ? 'active' : ''} 
            onClick={() => setActiveCategory('shoes')}
          >
            Shoes
          </button>
          <button 
            className={activeCategory === 'bags' ? 'active' : ''} 
            onClick={() => setActiveCategory('bags')}
          >
            Bags
          </button>
          <button 
            className={activeCategory === 'accessories' ? 'active' : ''} 
            onClick={() => setActiveCategory('accessories')}
          >
            Accessories
          </button>
        </div>

        <div className="sort-filter">
          <label htmlFor="sort">Sort by:</label>
          <select 
            id="sort" 
            value={sortBy} 
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="default">Default</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="name">Name</option>
          </select>
        </div>
      </div>

      <div className="products-grid">
        {sortedProducts.map((product, index) => (
          <div 
            key={product.id} 
            className="product-card"
            onClick={() => viewProductDetails(product.id)}
          >
            <div className="product-image">
              <img src={product.image} alt={product.title} />
              <button 
                className="quick-add-btn"
                onClick={(e) => handleAddToCart(product, e)}
              >
                Add to Cart
              </button>
            </div>
            <div className="product-info">
              <h3 className="product-title">{product.title}</h3>
              <p className="product-description">{product.description}</p>
              <div className="product-price">${product.price.toFixed(2)}</div>
            </div>
          </div>
        ))}
      </div>

      {sortedProducts.length === 0 && (
        <div className="no-products">
          <h3>No products found in this category</h3>
          <p>Try selecting a different category</p>
        </div>
      )}
    </div>
  );
};

// Product Detail Component
const ProductDetail = ({ addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [quantity, setQuantity] = useState(1);

  const product = products.find(p => p.id === parseInt(id));

  if (!product) {
    return <div className="product-not-found">Product not found</div>;
  }

  const handleAddToCart = () => {
    // Add the product to cart with the selected quantity
    for (let i = 0; i < quantity; i++) {
      addToCart(product);
    }

    // Show notification
    const notification = document.createElement('div');
    notification.className = 'cart-notification';
    notification.innerHTML = `
      <span>${quantity} ${product.title} added to cart!</span>
    `;
    document.body.appendChild(notification);

    // Remove notification after 2 seconds
    setTimeout(() => {
      notification.classList.add('fade-out');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 2000);
  };

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0) {
      setQuantity(value);
    }
  };

  return (
    <div className="product-detail-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        &larr; Back to Shop
      </button>

      <div className="product-detail">
        <div className="product-detail-image">
          <img src={product.image} alt={product.title} />
        </div>

        <div className="product-detail-info">
          <h1>{product.title}</h1>
          <div className="product-price">${product.price.toFixed(2)}</div>
          <p className="product-description">{product.description}</p>
          <div className="product-details">
            <h3>Product Details</h3>
            <p>{product.details}</p>
          </div>

          <div className="quantity-selector">
            <label htmlFor="quantity">Quantity:</label>
            <input 
              type="number" 
              id="quantity" 
              min="1" 
              value={quantity} 
              onChange={handleQuantityChange}
            />
          </div>

          <button className="add-to-cart-btn" onClick={handleAddToCart}>
            Add to Cart
          </button>

          <button className="continue-shopping" onClick={() => navigate('/shop')}>
            Continue Shopping
          </button>
        </div>
      </div>

      <div className="related-products">
        <h2>You Might Also Like</h2>
        <div className="related-products-grid">
          {products
            .filter(p => p.category === product.category && p.id !== product.id)
            .slice(0, 4)
            .map(relatedProduct => (
              <div 
                key={relatedProduct.id} 
                className="related-product-card"
                onClick={() => navigate(`/product/${relatedProduct.id}`)}
              >
                <img src={relatedProduct.image} alt={relatedProduct.title} />
                <h4>{relatedProduct.title}</h4>
                <div className="product-price">${relatedProduct.price.toFixed(2)}</div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item.id === product.id);
      if (existingItem) {
        return prevItems.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  return (
    <Router>
      <div className="app">
        <header className="app-header">
          <h1>Fashion Store</h1>
          <nav>
            <a href="/">Home</a>
            <a href="/shop">Shop</a>
            <a href="/cart">Cart ({cartItems.reduce((total, item) => total + item.quantity, 0)})</a>
          </nav>
        </header>

        <Routes>
          <Route path="/" element={
            <div className="home-page">
              <h2>Welcome to Our Store</h2>
              <p>Discover the latest fashion trends</p>
              <a href="/shop" className="cta-button">Shop Now</a>
            </div>
          } />
          <Route path="/shop" element={<Shop addToCart={addToCart} cartItems={cartItems} />} />
          <Route path="/product/:id" element={<ProductDetail addToCart={addToCart} />} />
          <Route path="/cart" element={
            <div className="cart-page">
              <h2>Your Shopping Cart</h2>
              {cartItems.length === 0 ? (
                <p>Your cart is empty</p>
              ) : (
                <div>
                  {cartItems.map(item => (
                    <div key={item.id} className="cart-item">
                      <img src={item.image} alt={item.title} />
                      <div>
                        <h3>{item.title}</h3>
                        <p>Quantity: {item.quantity}</p>
                        <p>Price: ${item.price * item.quantity}</p>
                      </div>
                    </div>
                  ))}
                  <div className="cart-total">
                    Total: ${cartItems.reduce((total, item) => total + (item.price * item.quantity), 0).toFixed(2)}
                  </div>
                </div>
              )}
            </div>
          } />
        </Routes>

        <footer className="app-footer">
          <p>&copy; 2023 Fashion Store. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
};

