    max-width: 800px;
    margin: 0 auto;
  }
  
  .blog-header {
    text-align: center;
    margin-bottom: 40px;
  }
  
  .category-badge {
    display: inline-block;
    background: #edf2f7;
    color: #4a5568;
    padding: 5px 15px;
    border-radius: 20px;
    font-size: 0.9rem;
    font-weight: 600;
    margin-bottom: 20px;
  }
  
  .blog-header h1 {
    font-size: 2.5rem;
    margin-bottom: 20px;
    color: #2d3748;
  }
  
  .blog-content {
    font-size: 1.1rem;
    line-height: 1.8;
    color: #4a5568;
  }
  
  .blog-content p {
    margin-bottom: 20px;
  }
  
  blockquote {
    border-left: 4px solid #e2e8f0;
    padding-left: 20px;
    margin: 30px 0;
    font-style: italic;
    color: #718096;
  }
  
  .blog-share {
    margin: 50px 0;
    text-align: center;
  }
  
  .blog-share h3 {
    margin-bottom: 15px;
    color: #2d3748;
  }
  
  .share-buttons {
    display: flex;
    justify-content: center;
    gap: 10px;
  }
  
  .share-buttons button {
    padding: 8px 16px;
    border: 1px solid #e2e8f0;
    background: #f7fafc;
    border-radius: 4px;
    cursor: pointer;
    transition: all 0.3s ease;
  }
  
  .share-buttons button:hover {
    background: #edf2f7;
  }
  
  .blog-navigation {
    margin: 40px 0;
  }
  
  .back-to-blog {
    display: inline-flex;
    align-items: center;
    color: #4a5568;
    text-decoration: none;
    font-weight: 600;
  }
  
  .back-to-blog:hover {
    color: #2d3748;
  }
  
  /* Related Posts */
  .related-posts {
    background: #f7fafc;
    padding: 60px 0;
  }
  
  .related-posts h2 {
    text-align: center;
    margin-bottom: 40px;
    color: #2d3748;
  }
  
  .related-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 30px;
  }
  
  .related-card {
    background: #fff;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0,0,0,0.05);
  }
  
  .related-card img {
    width: 100%;
    height: 180px;
    object-fit: cover;
  }
  
  .related-content {
    padding: 20px;
  }
  
  .related-content .category {
    display: inline-block;
    background: #edf2f7;
    color: #4a5568;
    padding: 3px 10px;
    border-radius: 15px;
    font-size: 0.8rem;
    font-weight: 600;
    margin-bottom: 10px;
  }
  
  .related-content h3 {
    font-size: 1.1rem;
    margin-bottom: 15px;
    color: #2d3748;
  }
  
  .related-content a {
    color: #4a5568;
    font-weight: 600;
    text-decoration: none;
  }
  
  .related-content a:hover {
    color: #2d3748;
  }
  
  /* Footer */
  .site-footer {
    background: #2d3748;
    color: #fff;
    padding: 30px 0;
    text-align: center;
  }
  
  /* Responsive Design */
  @media (max-width: 768px) {
    .blog-grid {
      grid-template-columns: 1fr;
    }
    
    .blog-header h1 {
      font-size: 2rem;
    }
    
    .blog-hero {
      height: 300px;
    }
    
    .related-grid {
      grid-template-columns: 1fr;
    }
    
    .main-nav .container {
      flex-direction: column;
      gap: 15px;
