import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  FiTruck, 
  FiShield, 
  FiHeart, 
  FiArrowRight, 
  FiGlobe, 
  FiTrendingUp,
  FiAward,
  FiUsers,
  FiInstagram,
  FiFacebook,
  FiTwitter
} from 'react-icons/fi';

const About = () => {
  const [hoveredCard, setHoveredCard] = useState(null);
  const [activeTab, setActiveTab] = useState('story');

  // Team members data
  const teamMembers = [
    {
      id: 1,
      name: 'Emma Wilson',
      role: 'Head Designer',
      image: 'https://images.unsplash.com/photo-1487412720507-e7ab37603c6f?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      bio: '10+ years in fashion design. Passionate about sustainable fashion and innovative textiles.'
    },
    {
      id: 2,
      name: 'James Carter',
      role: 'Retail Director',
      image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      bio: 'Expert in retail strategy and customer experience with a focus on sustainable practices.'
    },
    {
      id: 3,
      name: 'Sophia Lee',
      role: 'Creative Director',
      image: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      bio: 'Visionary creative leader with an eye for emerging trends and brand storytelling.'
    }
  ];

  // Values data
  const values = [
    {
      icon: <FiGlobe size={32} />,
      title: 'Sustainability',
      description: 'We prioritize eco-friendly materials and ethical manufacturing processes.'
    },
    {
      icon: <FiTrendingUp size={32} />,
      title: 'Quality',
      description: 'Every garment is crafted with attention to detail and built to last.'
    },
    {
      icon: <FiAward size={32} />,
      title: 'Style',
      description: 'We blend timeless elegance with contemporary trends.'
    },
    {
      icon: <FiUsers size={32} />,
      title: 'Community',
      description: 'We support local artists and give back to our community.'
    }
  ];

  // Animation variants
  const fadeIn = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } }
  };

  const staggerChildren = {
    visible: { transition: { staggerChildren: 0.1 } }
  };

  return (
    <div className="about-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <motion.div 
            className="hero-content"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
          >
            <h1>Style With Purpose</h1>
            <p>At ThreadCraft, we believe fashion should be sustainable, ethical, and accessible. Our clothing is designed to last, crafted with care, and made for real life.</p>
            <motion.button 
              whileHover={{ scale: 1.05, boxShadow: "0 10px 20px rgba(0,0,0,0.1)" }}
              whileTap={{ scale: 0.95 }}
              className="cta-button"
            >
              Shop Collection <FiArrowRight />
            </motion.button>
          </motion.div>
        </div>
      </section>

      {/* Story Section */}
      <section className="story-section">
        <div className="container">
          <div className="tabs">
            <button 
              className={activeTab === 'story' ? 'active' : ''}
              onClick={() => setActiveTab('story')}
            >
              Our Story
            </button>
            <button 
              className={activeTab === 'mission' ? 'active' : ''}
              onClick={() => setActiveTab('mission')}
            >
              Our Mission
            </button>
            <button 
              className={activeTab === 'sustainability' ? 'active' : ''}
              onClick={() => setActiveTab('sustainability')}
            >
              Sustainability
            </button>
          </div>

          <motion.div 
            key={activeTab}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className="tab-content"
          >
            {activeTab === 'story' && (
              <div className="content">
                <h2>From a Small Boutique to a Lifestyle Brand</h2>
                <p>ThreadCraft began in 2010 as a small boutique in Seattle, founded by fashion enthusiasts who believed in quality over quantity. What started as a curated selection of timeless pieces has grown into a complete lifestyle brand known for its commitment to sustainable practices and inclusive sizing.</p>
                <p>Today, we continue to honor our roots by supporting local artisans and maintaining the personal touch that defined our early days, while expanding our reach to bring conscious fashion to a global audience.</p>
              </div>
            )}
            {activeTab === 'mission' && (
              <div className="content">
                <h2>Redefining Fashion Responsibly</h2>
                <p>Our mission is to create clothing that respects both people and planet. We're committed to transparent supply chains, fair labor practices, and reducing our environmental footprint at every stage of production.</p>
                <p>We believe that looking good shouldn't come at the expense of our values, which is why we're constantly innovating to make sustainable fashion more accessible and desirable.</p>
              </div>
            )}
            {activeTab === 'sustainability' && (
              <div className="content">
                <h2>Our Commitment to the Planet</h2>
                <p>We've implemented numerous initiatives to minimize our environmental impact, including using organic and recycled materials, reducing water consumption in production, and implementing a circular fashion program that allows customers to return worn items for recycling.</p>
                <p>By 2025, we aim to become a carbon-neutral company and source 100% of our materials from sustainable sources.</p>
              </div>
            )}
          </motion.div>
        </div>
      </section>

      {/* Values Section */}
      <section className="values-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            <h2>Our Values</h2>
            <p>The principles that guide everything we create</p>
          </motion.div>

          <div className="values-grid">
            {values.map((value, index) => (
              <motion.div 
                key={index}
                className="value-card"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ 
                  y: -10,
                  transition: { duration: 0.2 }
                }}
              >
                <motion.div 
                  className="value-icon"
                  whileHover={{ rotate: 360, scale: 1.1 }}
                  transition={{ duration: 0.4 }}
                >
                  {value.icon}
                </motion.div>
                <h3>{value.title}</h3>
                <p>{value.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="team-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            <h2>Meet Our Team</h2>
            <p>The passionate people behind ThreadCraft</p>
          </motion.div>

          <div className="team-grid">
            {teamMembers.map((member) => (
              <motion.div 
                key={member.id}
                className="team-card"
                initial={{ opacity: 0, scale: 0.9 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5 }}
                viewport={{ once: true }}
                whileHover={{ y: -10, transition: { duration: 0.2 } }}
                onHoverStart={() => setHoveredCard(member.id)}
                onHoverEnd={() => setHoveredCard(null)}
              >
                <div className="image-container">
                  <img src={member.image} alt={member.name} />
                  <motion.div 
                    className="overlay"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: hoveredCard === member.id ? 0.7 : 0 }}
                    transition={{ duration: 0.3 }}
                  />
                  <motion.div 
                    className="bio"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: hoveredCard === member.id ? 1 : 0, y: hoveredCard === member.id ? 0 : 10 }}
                    transition={{ duration: 0.3 }}
                  >
                    <p>{member.bio}</p>
                  </motion.div>
                </div>
                <h3>{member.name}</h3>
                <p>{member.role}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Commitment Section */}
      <section className="commitment-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            <h2>Our Commitment</h2>
            <p>How we're making a difference</p>
          </motion.div>

          <div className="commitment-grid">
            <motion.div 
              className="commitment-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              viewport={{ once: true }}
              whileHover={{ y: -5, transition: { duration: 0.2 } }}
            >
              <FiTruck size={40} />
              <h3>Carbon Neutral Shipping</h3>
              <p>All our shipments are carbon neutral through our partnership with environmental organizations.</p>
            </motion.div>

            <motion.div 
              className="commitment-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.1 }}
              viewport={{ once: true }}
              whileHover={{ y: -5, transition: { duration: 0.2 } }}
            >
              <FiShield size={40} />
              <h3>Ethical Production</h3>
              <p>We ensure fair wages and safe working conditions at all our partner factories.</p>
            </motion.div>

            <motion.div 
              className="commitment-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
              viewport={{ once: true }}
              whileHover={{ y: -5, transition: { duration: 0.2 } }}
            >
              <FiHeart size={40} />
              <h3>Community Support</h3>
              <p>5% of all profits are donated to organizations supporting garment workers and environmental causes.</p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <motion.div 
            className="cta-content"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            <h2>Join Our Community</h2>
            <p>Subscribe to our newsletter for exclusive offers, styling tips, and updates on our sustainability journey</p>
            <div className="newsletter-form">
              <input type="email" placeholder="Your email address" />
              <motion.button 
                whileHover={{ scale: 1.05, boxShadow: "0 10px 20px rgba(0,0,0,0.15)" }}
                whileTap={{ scale: 0.95 }}
                className="cta-button"
              >
                Subscribe
              </motion.button>
            </div>
            <div className="social-links">
              <a href="#"><FiInstagram /></a>
              <a href="#"><FiFacebook /></a>
              <a href="#"><FiTwitter /></a>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default About;
