import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  FiUsers, 
  FiAward, 
  FiBriefcase, 
  FiHeart, 
  FiArrowRight, 
  FiGlobe, 
  FiTrendingUp,
  FiCode,
  FiLayers,
  FiLinkedin,
  FiTwitter,
  FiGithub
} from 'react-icons/fi';
import './About.css';

const About = () => {
  const [hoveredCard, setHoveredCard] = useState(null);
  const [activeTab, setActiveTab] = useState('mission');

  // Team members data
  const teamMembers = [
    {
      id: 1,
      name: 'Sarah Johnson',
      role: 'CEO & Founder',
      image: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      bio: '10+ years of experience in tech industry. Passionate about innovation and building products that make a difference.',
      social: {
        linkedin: '#',
        twitter: '#',
        github: '#'
      }
    },
    {
      id: 2,
      name: 'Michael Chen',
      role: 'Lead Developer',
      image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      bio: 'Full-stack developer with expertise in React, Node.js, and cloud architecture. Loves solving complex problems.',
      social: {
        linkedin: '#',
        twitter: '#',
        github: '#'
      }
    },
    {
      id: 3,
      name: 'Emma Rodriguez',
      role: 'Creative Director',
      image: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      bio: 'Design expert with a keen eye for user experience and aesthetics. Believes in design that serves both business and user needs.',
      social: {
        linkedin: '#',
        twitter: '#',
        github: '#'
      }
    },
    {
      id: 4,
      name: 'David Kim',
      role: 'Marketing Head',
      image: 'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      bio: 'Digital marketing strategist with proven track record of growth. Specializes in data-driven marketing campaigns.',
      social: {
        linkedin: '#',
        twitter: '#',
        github: '#'
      }
    }
  ];

  // Stats data
  const stats = [
    { icon: <FiUsers size={24} />, number: '250+', label: 'Happy Clients' },
    { icon: <FiBriefcase size={24} />, number: '500+', label: 'Projects Completed' },
    { icon: <FiAward size={24} />, number: '25+', label: 'Awards Won' },
    { icon: <FiHeart size={24} />, number: '99%', label: 'Client Satisfaction' }
  ];

  // Values data
  const values = [
    {
      icon: <FiGlobe size={32} />,
      title: 'Global Mindset',
      description: 'We think and act globally, embracing diverse perspectives and cultures in everything we do.'
    },
    {
      icon: <FiTrendingUp size={32} />,
      title: 'Continuous Growth',
      description: 'We believe in constant learning, improvement, and pushing the boundaries of what\'s possible.'
    },
    {
      icon: <FiCode size={32} />,
      title: 'Technical Excellence',
      description: 'We strive for the highest quality in everything we build, paying attention to every detail.'
    },
    {
      icon: <FiLayers size={32} />,
      title: 'Collaboration',
      description: 'We achieve more by working together effectively, valuing each team member\'s contribution.'
    }
  ];

  // Clients data
  const clients = [
    { name: 'Google', logo: 'https://logo.clearbit.com/google.com' },
    { name: 'Microsoft', logo: 'https://logo.clearbit.com/microsoft.com' },
    { name: 'Amazon', logo: 'https://logo.clearbit.com/amazon.com' },
    { name: 'Netflix', logo: 'https://logo.clearbit.com/netflix.com' },
    { name: 'Spotify', logo: 'https://logo.clearbit.com/spotify.com' },
    { name: 'Adobe', logo: 'https://logo.clearbit.com/adobe.com' }
  ];

  // Animation variants
  const fadeIn = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } }
  };

  const staggerChildren = {
    visible: { transition: { staggerChildren: 0.1 } }
  };

  return (
    <div className="about-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <motion.div 
            className="hero-content"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
          >
            <h1>We Create Digital Experiences That Matter</h1>
            <p>We are a team of passionate designers, developers, and strategists dedicated to building innovative solutions that drive growth and transformation for businesses worldwide.</p>
            <motion.button 
              whileHover={{ scale: 1.05, boxShadow: "0 10px 20px rgba(0,0,0,0.1)" }}
              whileTap={{ scale: 0.95 }}
              className="cta-button"
            >
              Explore Our Work <FiArrowRight />
            </motion.button>
          </motion.div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container">
          <motion.div 
            className="stats-grid"
            variants={staggerChildren}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, margin: "-100px" }}
          >
            {stats.map((stat, index) => (
              <motion.div 
                key={index} 
                className="stat-card"
                variants={fadeIn}
                whileHover={{ y: -5, transition: { duration: 0.2 } }}
              >
                <div className="stat-icon">{stat.icon}</div>
                <h3>{stat.number}</h3>
                <p>{stat.label}</p>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </section>

      {/* Mission & Story Section */}
      <section className="mission-section">
        <div className="container">
          <div className="tabs">
            <button 
              className={activeTab === 'mission' ? 'active' : ''}
              onClick={() => setActiveTab('mission')}
            >
              Our Mission
            </button>
            <button 
              className={activeTab === 'story' ? 'active' : ''}
              onClick={() => setActiveTab('story')}
            >
              Our Story
            </button>
            <button 
              className={activeTab === 'vision' ? 'active' : ''}
              onClick={() => setActiveTab('vision')}
            >
              Our Vision
            </button>
          </div>

          <motion.div 
            key={activeTab}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className="tab-content"
          >
            {activeTab === 'mission' && (
              <div className="content">
                <h2>Empowering Businesses Through Technology</h2>
                <p>Our mission is to leverage cutting-edge technology to solve complex business challenges and create meaningful digital experiences that connect with users and drive measurable results.</p>
                <p>We believe in the power of collaboration, innovation, and excellence to transform ideas into impactful solutions that make a difference in people's lives and businesses.</p>
              </div>
            )}
            {activeTab === 'story' && (
              <div className="content">
                <h2>From Humble Beginnings to Industry Leaders</h2>
                <p>Founded in 2015, we started as a small team of three with a shared vision to make quality digital solutions accessible to businesses of all sizes. Our first office was a small garage where we worked tirelessly to deliver exceptional value to our early clients.</p>
                <p>Over the years, we've grown into a diverse team of experts, completed hundreds of projects, and built lasting relationships with clients across the globe. Our journey has been marked by challenges, learning, and growth that have shaped who we are today.</p>
              </div>
            )}
            {activeTab === 'vision' && (
              <div className="content">
                <h2>Shaping the Future of Digital Innovation</h2>
                <p>We envision a future where technology seamlessly enhances human experiences, breaks down barriers, and creates opportunities for growth and connection across the globe.</p>
                <p>Our goal is to be at the forefront of this transformation, pioneering new approaches and setting new standards in the digital landscape while maintaining our commitment to ethical practices and positive impact.</p>
              </div>
            )}
          </motion.div>
        </div>
      </section>

      {/* Values Section */}
      <section className="values-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true, margin: "-100px" }}
          >
            <h2>Our Core Values</h2>
            <p>These principles guide everything we do and shape our company culture</p>
          </motion.div>

          <div className="values-grid">
            {values.map((value, index) => (
              <motion.div 
                key={index}
                className="value-card"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                viewport={{ once: true, margin: "-100px" }}
                whileHover={{ 
                  y: -10,
                  transition: { duration: 0.2 }
                }}
              >
                <motion.div 
                  className="value-icon"
                  whileHover={{ rotate: 360, scale: 1.1 }}
                  transition={{ duration: 0.4 }}
                >
                  {value.icon}
                </motion.div>
                <h3>{value.title}</h3>
                <p>{value.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="team-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true, margin: "-100px" }}
          >
            <h2>Meet Our Team</h2>
            <p>Talented professionals dedicated to your success</p>
          </motion.div>

          <div className="team-grid">
            {teamMembers.map((member) => (
              <motion.div 
                key={member.id}
                className="team-card"
                initial={{ opacity: 0, scale: 0.9 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5 }}
                viewport={{ once: true, margin: "-100px" }}
                whileHover={{ y: -10, transition: { duration: 0.2 } }}
                onHoverStart={() => setHoveredCard(member.id)}
                onHoverEnd={() => setHoveredCard(null)}
              >
                <div className="image-container">
                  <img src={member.image} alt={member.name} />
                  <motion.div 
                    className="overlay"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: hoveredCard === member.id ? 0.7 : 0 }}
                    transition={{ duration: 0.3 }}
                  />
                  <motion.div 
                    className="bio"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: hoveredCard === member.id ? 1 : 0, y: hoveredCard === member.id ? 0 : 10 }}
                    transition={{ duration: 0.3 }}
                  >
                    <p>{member.bio}</p>
                    <div className="social-links">
                      <a href={member.social.linkedin}><FiLinkedin /></a>
                      <a href={member.social.twitter}><FiTwitter /></a>
                      <a href={member.social.github}><FiGithub /></a>
                    </div>
                  </motion.div>
                </div>
                <h3>{member.name}</h3>
                <p>{member.role}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Clients Section */}
      <section className="clients-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true, margin: "-100px" }}
          >
            <h2>Our Clients</h2>
            <p>We've had the privilege of working with amazing companies</p>
          </motion.div>

          <div className="clients-grid">
            {clients.map((client, index) => (
              <motion.div 
                key={index}
                className="client-logo"
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.4, delay: index * 0.1 }}
                viewport={{ once: true, margin: "-100px" }}
                whileHover={{ scale: 1.1, transition: { duration: 0.2 } }}
              >
                <img src={client.logo} alt={client.name} />
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <motion.div 
            className="cta-content"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true, margin: "-100px" }}
          >
            <h2>Ready to Start Your Project?</h2>
            <p>Let's work together to bring your ideas to life</p>
            <motion.button 
              whileHover={{ scale: 1.05, boxShadow: "0 10px 20px rgba(0,0,0,0.15)" }}
              whileTap={{ scale: 0.95 }}
              className="cta-button"
            >
              Get in Touch <FiArrowRight />
            </motion.button>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default About;
