import React from 'react';
import ProductCard from '../components/ProductCard';

const Shop = () => {
    const products = [
        // Sample product data
        { id: 1, title: 'T-Shirt', price: 19.99, image: 'path/to/image1.jpg' },
        { id: 2, title: 'Jeans', price: 39.99, image: 'path/to/image2.jpg' },
        { id: 3, title: 'Jacket', price: 59.99, image: 'path/to/image3.jpg' },
        // Add more products as needed
    ];

    return (
        <div className="shop">
            <h1>Shop Our Collection</h1>
            <div className="product-list">
                {products.map(product => (
                    <ProductCard key={product.id} product={product} />
                ))}
            </div>
        </div>
    );
};

export default Shop;
