import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProductDetail.css';

const ProductDetailPage = ({ addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [quantity, setQuantity] = useState(1);

  // Sample product data (same as in Shop.jsx)
  const products = [
    { id: 1, title: 'Classic White T-Shirt', price: 24.99, image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Premium cotton t-shirt with perfect fit for everyday wear.', details: '100% premium cotton, machine washable, available in all sizes.' },
    { id: 2, title: 'Slim Fit Jeans', price: 59.99, image: 'https://images.unsplash.com/photo-1542272604-787c3835535d?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Comfortable slim fit jeans with stretch for all-day comfort.', details: '98% cotton, 2% elastane, stretch denim, five-pocket style.' },
    { id: 3, title: 'Summer Floral Dress', price: 45.99, image: 'https://images.unsplash.com/photo-1515372039744-b8f02a3ae446?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'women', description: 'Lightweight floral dress perfect for summer occasions.', details: 'Lightweight chiffon fabric, floral pattern, available in multiple colors.' },
    { id: 4, title: 'Designer Handbag', price: 89.99, image: 'https://images.unsplash.com/photo-1584917865442-de89df76afd3?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'bags', description: 'Elegant handbag with ample space for all your essentials.', details: 'Genuine leather, gold-tone hardware, inner zip pocket.' },
    { id: 5, title: 'Running Shoes', price: 79.99, image: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'shoes', description: 'High-performance running shoes with cushion technology.', details: 'Breathable mesh upper, cushioned midsole, rubber outsole for traction.' },
    { id: 6, title: 'Leather Jacket', price: 129.99, image: 'https://images.unsplash.com/photo-1551028719-00167b16eac5?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Genuine leather jacket with a classic biker style.', details: '100% genuine leather, front zip closure, ribbed cuffs and waistband.' },
    { id: 7, title: 'High Heels', price: 64.99, image: 'https://images.unsplash.com/photo-1543163521-1bf539c55dd2?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'shoes', description: 'Elegant high heels for special occasions.', details: 'Suede material, 3-inch heel, pointed toe design.' },
    { id: 8, title: 'Crossbody Bag', price: 49.99, image: 'https://images.unsplash.com/photo-1591561954557-26941169b49e?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'bags', description: 'Compact crossbody bag with adjustable strap.', details: 'Faux leather, adjustable strap, multiple interior pockets.' },
    { id: 9, title: 'Casual Blouse', price: 39.99, image: 'https://images.unsplash.com/photo-1594633312681-425c7b97ccd1?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'women', description: 'Comfortable and stylish blouse for everyday wear.', details: 'Soft viscose fabric, button-front design, relaxed fit.' },
    { id: 10, title: 'Sports Shorts', price: 34.99, image: 'https://images.unsplash.com/photo-1506629905877-52a5ca6d63b1?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'men', description: 'Breathable sports shorts for your active lifestyle.', details: 'Lightweight polyester, elastic waistband with drawstring, side pockets.' },
    { id: 11, title: 'Earrings Set', price: 29.99, image: 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'accessories', description: 'Beautiful set of earrings for any occasion.', details: 'Hypoallergenic materials, set of 3 different styles.' },
    { id: 12, title: 'Sunglasses', price: 89.99, image: 'https://images.unsplash.com/photo-1572635196237-14b3f281503f?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80', category: 'accessories', description: 'Stylish sunglasses with UV protection.', details: '100% UV protection, polarized lenses, lightweight frame.' },
  ];

  const product = products.find(p => p.id === parseInt(id));

  if (!product) {
    return <div className="product-not-found">Product not found</div>;
  }

  const handleAddToCart = () => {
    // Add the product to cart with the selected quantity
    for (let i = 0; i < quantity; i++) {
      addToCart(product);
    }

    // Show notification
    const notification = document.createElement('div');
    notification.className = 'cart-notification';
    notification.innerHTML = `
      <span>${quantity} ${product.title} added to cart!</span>
    `;
    document.body.appendChild(notification);

    // Remove notification after 2 seconds
    setTimeout(() => {
      notification.classList.add('fade-out');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 2000);
  };

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0) {
      setQuantity(value);
    }
  };

  return (
    <div className="product-detail-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        &larr; Back to Shop
      </button>

      <div className="product-detail">
        <div className="product-detail-image">
          <img src={product.image} alt={product.title} />
        </div>

        <div className="product-detail-info">
          <h1>{product.title}</h1>
          <div className="product-price">${product.price.toFixed(2)}</div>
          <p className="product-description">{product.description}</p>
          <div className="product-details">
            <h3>Product Details</h3>
            <p>{product.details}</p>
          </div>

          <div className="quantity-selector">
            <label htmlFor="quantity">Quantity:</label>
            <input 
              type="number" 
              id="quantity" 
              min="1" 
              value={quantity} 
              onChange={handleQuantityChange}
            />
          </div>

          <button className="add-to-cart-btn" onClick={handleAddToCart}>
            Add to Cart
          </button>

          <button className="continue-shopping" onClick={() => navigate('/shop')}>
            Continue Shopping
          </button>
        </div>
      </div>

      <div className="related-products">
        <h2>You Might Also Like</h2>
        <div className="related-products-grid">
          {products
            .filter(p => p.category === product.category && p.id !== product.id)
            .slice(0, 4)
            .map(relatedProduct => (
              <div 
                key={relatedProduct.id} 
                className="related-product-card"
                onClick={() => navigate(`/product/${relatedProduct.id}`)}
              >
                <img src={relatedProduct.image} alt={relatedProduct.title} />
                <h4>{relatedProduct.title}</h4>
                <div className="product-price">${relatedProduct.price.toFixed(2)}</div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
