import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [deals, setDeals] = useState([]);

  // Sample data - in a real app, this would come from an API
  useEffect(() => {
    // Mock data for deals
    const mockDeals = [
      { id: 1, name: 'Summer T-Shirts', discount: '60% off', category: 'men' },
      { id: 2, name: 'Designer Handbags', discount: 'Buy 2 Get 1 Free', category: 'women' },
      { id: 3, name: 'Smart Watches', discount: '40% off', category: 'gadgets' },
      { id: 4, name: 'Running Shoes', discount: '50% off', category: 'men' },
      { id: 5, name: 'Sunglasses', discount: '30% off', category: 'women' },
    ];
    setDeals(mockDeals);
  }, []);

  // Banner slides
  const bannerSlides = [
    {
      id: 1,
      title: 'Summer Sale',
      subtitle: 'Up to 70% off on summer essentials',
      image: 'https://images.unsplash.com/photo-1523381210434-271e8be1f52b?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      link: '/sale'
    },
    {
      id: 2,
      title: 'New Gadgets',
      subtitle: 'Latest tech at amazing prices',
      image: 'https://images.unsplash.com/photo-1498049794561-7780e7231661?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      link: '/gadgets'
    },
    {
      id: 3,
      title: 'Winter Collection',
      subtitle: 'Stay warm in style',
      image: 'https://images.unsplash.com/photo-1441984904996-e0b6ba687e04?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      link: '/winter'
    }
  ];

  // Auto-rotate banners
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev === bannerSlides.length - 1 ? 0 : prev + 1));
    }, 5000);
    return () => clearInterval(interval);
  }, [bannerSlides.length]);

  // Featured categories
  const categories = [
    {
      id: 1,
      name: "Men's Fashion",
      image: "https://images.unsplash.com/photo-1617137968427-85924c800a22?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80",
      link: "/mens"
    },
    {
      id: 2,
      name: "Women's Fashion",
      image: "https://images.unsplash.com/photo-1581044777550-4cfa60707c03?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80",
      link: "/womens"
    },
    {
