import React, { useState } from 'react';
import './Header.css';

const Header = () => {
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Sample cart items
  const [cartItems] = useState([
    { id: 1, name: 'Premium T-Shirt', price: 29.99, quantity: 2, image: 'https://via.placeholder.com/60x60' },
    { id: 2, name: 'Designer Jeans', price: 89.99, quantity: 1, image: 'https://via.placeholder.com/60x60' }
  ]);

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const cartTotal = cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  const cartItemCount = cartItems.reduce((count, item) => count + item.quantity, 0);

  return (
    <>
      <header className="clothing-header">
        <div className="header-top">
          <p>Free shipping on orders over $50</p>
        </div>

        <div className="header-main">
          <div className="header-container">
            <div className="logo">
              <a href="/">FASHIONISTA</a>
            </div>

            <nav className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
              <a href="/">Home</a>
              <a href="/about">About</a>
              <a href="/shop">Shop</a>
              <a href="/contact">Contact</a>
              <a href="/blog">Blog</a>
            </nav>

            <div className="header-actions">
              <a href="/account" className="account-link">
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                  <path fillRule="evenodd" d="M7.5 6a4.5 4.5 0 119 0 4.5 4.5 0 01-9 0zM3.751 20.105a8.25 8.25 0 0116.498 0 .75.75 0 01-.437.695A18.683 18.683 0 0112 22.5c-2.786 0-5.433-.608-7.812-1.7a.75.75 0 01-.437-.695z" clipRule="evenodd" />
                </svg>
              </a>

              <button className="cart-btn" onClick={toggleCart}>
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M2.25 2.25a.75.75 0 000 1.5h1.386c.17 0 .318.114.362.278l2.558 9.592a3.752 3.752 0 00-2.806 3.63c0 .414.336.75.75.75h15.75a.75.75 0 000-1.5H5.378A2.25 2.25 0 017.5 15h11.218a.75.75 0 00.674-.421 60.358 60.358 0 002.96-7.228.75.75 0 00-.525-.965A60.864 60.864 0 005.68 4.509l-.232-.867A1.875 1.875 0 003.636 2.25H2.25zM3.75 20.25a1.5 1.5 0 113 0 1.5 1.5 0 01-3 0zM16.5 20.25a1.5 1.5 0 113 0 1.5 1.5 0 01-3 0z" />
                </svg>
                {cartItemCount > 0 && <span className="cart-count">{cartItemCount}</span>}
              </button>

              <button className="menu-toggle" onClick={toggleMenu}>
                <span></span>
                <span></span>
                <span></span>
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Modern Cart Dropdown */}
      <div className={`cart-dropdown ${isCartOpen ? 'active' : ''}`}>
        <div className="cart-header">
          <h3>Your Cart ({cartItemCount})</h3>
          <button className="close-cart" onClick={toggleCart}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
              <path fillRule="evenodd" d="M5.47 5.47a.75.75 0 011.06 0L12 10.94l5.47-5.47a.75.75 0 111.06 1.06L13.06 12l5.47 5.47a.75.75 0 11-1.06 1.06L12 13.06l-5.47 5.47a.75.75 0 01-1.06-1.06L10.94 12 5.47 6.53a.75.75 0 010-1.06z" clipRule="evenodd" />
            </svg>
          </button>
        </div>

        <div className="cart-items">
          {cartItems.length > 0 ? (
            cartItems.map(item => (
              <div className="cart-item" key={item.id}>
                <img src={item.image} alt={item.name} />
                <div className="item-details">
                  <h4>{item.name}</h4>
                  <p>${item.price} x {item.quantity}</p>
                </div>
                <div className="item-total">${(item.price * item.quantity).toFixed(2)}</div>
              </div>
            ))
          ) : (
            <p className="empty-cart">Your cart is empty</p>
          )}
        </div>

        <div className="cart-footer">
          <div className="cart-total">
            <span>Subtotal:</span>
            <span>${cartTotal.toFixed(2)}</span>
          </div>
          <div className="cart-actions">
            <a href="/cart" className="view-cart-btn">View Cart</a>
            <a href="/checkout" className="checkout-btn">Checkout</a>
          </div>
        </div>
      </div>

      {/* Overlay when cart is open */}
      {isCartOpen && <div className="overlay" onClick={toggleCart}></div>}
    </>
  );
};

export default Header;
