import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import './SingleBlogPage.css';

const SingleBlogPage = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [relatedPosts, setRelatedPosts] = useState([]);

  // Sample blog data - in a real app, this would come from an API
  const blogData = [
    {
      id: 1,
      title: "Spring Fashion Trends 2023",
      excerpt: "Discover the hottest fashion trends for spring 2023 that will transform your wardrobe",
      content: `
        <p>Spring 2023 brings a refreshing mix of bold colors, innovative textures, and nostalgic silhouettes. This season, fashion is all about self-expression and embracing individuality.</p>
        
        <p>One of the standout trends is the return of vibrant pastels. From soft lavender to mint green, these hues are appearing in everything from tailored suits to flowing dresses. Designers are encouraging us to experiment with color in unexpected ways.</p>
        
        <p>Another significant trend is the emphasis on sustainable materials. Many brands are introducing pieces made from recycled fabrics and innovative eco-friendly materials, proving that style and sustainability can coexist beautifully.</p>
        
        <h3>Key Trends to Try:</h3>
        <ul>
          <li>Oversized blazers with strong shoulders</li>
          <li>Sheer fabrics layered over minimalist pieces</li>
          <li>Utility details on dresses and separates</li>
          <li>Statement accessories in bold, geometric shapes</li>
        </ul>
        
        <p>Whether you're refreshing your entire wardrobe or just adding a few key pieces, these trends offer plenty of inspiration for creating looks that feel both current and uniquely you.</p>
      `,
      author: "Emma Johnson",
      date: "March 15, 2023",
      image: "https://images.unsplash.com/photo-1483985988355-763728e1935b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1470&q=80",
      category: "Trends"
    },
    {
      id: 2,
      title: "Sustainable Fashion: A Complete Guide",
      excerpt: "Learn how to build an eco-friendly wardrobe without sacrificing style",
      content: "<p>Sustainable fashion content...</p>",
      author: "Michael Chen",
      date: "April 2, 2023",
      image: "https://images.unsplash.com/photo-1603400521630-9f2de124b33b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1494&q=80",
      category: "Sustainability"
    },
    {
      id: 3,
      title: "Accessorizing Your Summer Outfits",
      excerpt: "The perfect accessories to elevate your summer wardrobe",
      content: "<p>Summer accessories content...</p>",
      author: "Sophia Williams",
      date: "May 10, 2023",
      image: "https://images.unsplash.com/photo-1539109136881-3be0616acf4b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1374&q=80",
      category: "Accessories"
    },
    {
      id: 4,
      title: "Minimalist Wardrobe Essentials",
      excerpt: "Building a capsule wardrobe with timeless pieces",
      content: "<p>Minimalist wardrobe content...</p>",
      author: "David Kim",
      date: "June 5, 2023",
      image: "https://images.unsplash.com/photo-1566206091558-7f218b696731?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1364&q=80",
      category: "Style"
    }
  ];

  useEffect(() => {
    // Find the current blog post
    const currentBlog = blogData.find(b => b.id === parseInt(id));
    setBlog(currentBlog);
    
    // Find related posts (excluding the current one)
    const related = blogData
      .filter(b => b.id !== parseInt(id))
      .slice(0, 3);
    setRelatedPosts(related);
  }, [id]);

  if (!blog) {
    return (
      <div className="container">
        <h2>Blog post not found</h2>
        <Link to="/blog" className="back-to-blog">
          &larr; Back to all posts
        </Link>
      </div>
    );
  }

  const handleShare = (platform) => {
    const shareUrl = encodeURIComponent(window.location.href);
    const shareText = encodeURIComponent(blog.title);
    
    let shareWindowUrl;
    
    switch(platform) {
      case 'twitter':
        shareWindowUrl = `https://twitter.com/intent/tweet?text=${shareText}&url=${shareUrl}`;
        break;
      case 'facebook':
        shareWindowUrl = `https://www.facebook.com/sharer/sharer.php?u=${shareUrl}`;
        break;
      case 'pinterest':
        shareWindowUrl = `https://pinterest.com/pin/create/button/?url=${shareUrl}&description=${shareText}`;
        break;
      case 'email':
        shareWindowUrl = `mailto:?subject=${shareText}&body=Check out this article: ${shareUrl}`;
        break;
      default:
        return;
    }
    
    window.open(shareWindowUrl, '_blank');
  };

  return (
    <div className="single-blog-page">
      <article className="single-blog">
        <div className="blog-hero">
          <img src={blog.image} alt={blog.title} />
        </div>
        
        <div className="container">
          <div className="blog-content-wrapper">
            <div className="blog-header">
              <span className="category-badge">{blog.category}</span>
              <h1>{blog.title}</h1>
              <div className="blog-meta">
                <span className="author">By {blog.author}</span>
                <span className="date">{blog.date}</span>
              </div>
            </div>
            
            <div 
              className="blog-content" 
              dangerouslySetInnerHTML={{ __html: blog.content }} 
            />
            
            <div className="blog-share">
              <h3>Share this post</h3>
              <div className="share-buttons">
                <button onClick={() => handleShare('twitter')}>
                  <i className="fab fa-twitter"></i> Twitter
                </button>
                <button onClick={() => handleShare('facebook')}>
                  <i className="fab fa-facebook-f"></i> Facebook
                </button>
                <button onClick={() => handleShare('pinterest')}>
                  <i className="fab fa-pinterest"></i> Pinterest
                </button>
                <button onClick={() => handleShare('email')}>
                  <i className="far fa-envelope"></i> Email
                </button>
              </div>
            </div>
            
            <div className="blog-navigation">
              <Link to="/blog" className="back-to-blog">
                &larr; Back to all posts
              </Link>
            </div>
          </div>
        </div>
      </article>
      
      <section className="related-posts">
        <div className="container">
          <h2>You might also like</h2>
          <div className="related-grid">
            {relatedPosts.map(post => (
              <div key={post.id} className="related-card">
                <img src={post.image} alt={post.title} />
                <div className="related-content">
                  <span className="category">{post.category}</span>
                  <h3>{post.title}</h3>
                  <p>{post.excerpt}</p>
                  <Link to={`/blog/${post.id}`} className="read-more">
                    Read More
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default SingleBlogPage;
